CREATE TABLE Employee (
    Employee_ID SERIAL PRIMARY KEY,
    Name VARCHAR(50),
    Role VARCHAR(50),
    Department VARCHAR(50),
    HireDate DATE,
    Salary MONEY,
    BirthDate DATE,
    Phone VARCHAR(50),
    Address_ID INT REFERENCES Address(Address_ID),
    Station_ID VARCHAR(7) REFERENCES Station(Station_ID)
);


UPDATE Employee
SET Employee_ID = Employee_ID + 1000;

